@keyframes fade-bg-color {
  0% {
    background-color: white;
  }
  50% {
    background-color: transparent;
  }
  100% {
    background-color: white;
  }
}

.process-wrap {
  border-width: 1px;
  border-color: white;
  border-style: solid;
  border-radius: 3px;
  height: 0.5rem;
  width: 100%;
  min-height: 16px;

  >.process-entity {
    margin: 3px;
    background-color: white;
    height: calc(100% - 6px);
    width: calc(100% - 6px);

    animation-name: fade-bg-color;
    animation-duration: 6s;
    animation-timing-function: linear;
    animation-iteration-count: infinite;
  }
}
.number-span {
  width: 3em;
  text-align: right;
  font-size: 0.4rem;
  text-indent: 16px;
  display: block;
  position: relative;
  white-space: nowrap;
}
